static void theme_set (s_image_t *img, unsigned int *data, int a, int b)
{
	img->w = a;
	img->h = b;
	img->rgba = (unsigned int *) s_malloc(a * b * sizeof(unsigned int) + 1);
	memcpy(img->rgba, data, a * b * sizeof(unsigned int));
}

